document.addEventListener("DOMContentLoaded",function(){const profileIcon=document.getElementById("profileIcon");const helloText=document.getElementById("helloText");const user={firstName:"Chibuzor",lastName:"Daniel",profilePicture:""};if(user.profilePicture){profileIcon.style.backgroundImage=`url(${user.profilePicture})`;profileIcon.style.backgroundSize="cover"}else{profileIcon.innerText=user.firstName.charAt(0)+user.lastName.charAt(0)}if(helloText){helloText.innerText=`Hello, ${user.firstName}`}});